INSERT INTO faculty (name,designation,responsibility,phoneno,emailid,researcharea,website) values ( ' Rajat Subhra Chakraborty',' Associate Professor','NULL',' +91-3222-281792 ',' rschakraborty@cse.iitkgp.ernet.in ',' My research interests are in the areas ofHardware Security,Application of Novel Nano-devices to Hardware Security, VLSI Design and Methodologies andDigital Multimedia Security. I am part of the &quot;Secured Embedded Architecture Laboratory&quot; (SEAL), where we focus on applied security research. For more details about my publications, please refer tohttps://sites.google.com/site/rschakraborty/andhttps://sites.google.com/site/rschakraborty/.',' "https:')
