INSERT INTO faculty (name,designation,responsibility,phoneno,emailid,researcharea,website) values ( ' Sudebkumar Prasant Pal',' Professor','NULL',' +91-3222-283488 ',' spp@cse.iitkgp.ernet.in ',' My current research encompasses the design of approximation algorithms for (i) computing diffuse reflection paths in polygons, and (ii) computing vertex guard sets for guarding polygons. I am also working on estimating bounds on the sizes of separating and bisecting families for set systems (hypergraphs). ',' "http://cse.iitkgp.ac.in/~spp/"')
